orm>\r\n    <!-- END news_add_comment -->\r\n\r\n    <!-- BEGIN news_no_comment -->\r\n    <div id=\"news-no-comments\">{TXT_NEWS_COMMENTS_NONE_EXISTING}</div>\r\n    <!-- END news_no_comment -->\r\n\r\n    <!-- BEGIN news_comment_list -->\r\n        <!-- BEGIN news_comment -->\r\n        <div class=\"news-comment\">\r\n            <div class=\"news-comment-poster\">{NEWS_COMMENTS_LONG_DATE} | {NEWS_COMMENTS_POSTER}</div>\r\n            <div class=\"news-comment-title\"><strong>{NEWS_COMMENTS_TITLE}</strong></div>\r\n            <div class=\"news-comment-message\">{NEWS_COMMENTS_MESSAGE}</div>\r\n        </div>\r\n        <!-- END news_comment -->\r\n    <!-- END news_comment_list -->\r\n<!-- END news_comments -->\r\n\r\n<script type=\"text/javascript\">\r\n    window.___gcfg = {lang: \'de\'};\r\n\r\n    (function () {\r\n        var po = document.createElement(\'script\'); po.type = \'text/javascript\'; po.async = true;\r\n        po.src = \'https://apis.google.com/js/plusone.js\';\r\n        var s = document.getElementsByTagName(\'script\')[0]; s.parentNode.insertBefore(po, s);\r\n    })();\r\n\r\n    (function (d, s, id) {\r\n        var js, fjs = d.getElementsByTagName(s)[0];\r\n        if (d.getElementById(id)) return;\r\n        js = d.createElement(s); js.id = id;\r\n        js.src = \"//connect.facebook.net/de_DE/all.js#xfbml=1\";\r\n        fjs.parentNode.insertBefore(js, fjs);\r\n    }(document, \'script\', \'facebook-jssdk\'));\r\n\r\n    !function (d,s,id) {\r\n        var js,fjs = d.getElementsByTagName(s)[0];\r\n        if (!d.getElementById(id)) {\r\n            js = d.createElement(s);\r\n            js.id=id;js.src=\"//platform.twitter.com/widgets.js\";\r\n            fjs.parentNode.insertBefore(js,fjs);\r\n        }\r\n    }(document,\"script\",\"twitter-wjs\");\r\n\r\n    ;(function (d, s) {\r\n        var x = d.createElement(s),\r\n        s = d.getElementsByTagName(s)[0];\r\n        x.src =\'https://www.xing-share.com/js/external/share.js\';\r\n