opProductOptionsValuesRow -->\r\n                                <strong>\r\n                                    {SHOP_PRODUCT_OPTIONS_NAME}\r\n                                    <!-- BEGIN product_attribute_mandatory -->\r\n                                    <span class=\"mandatory\">&nbsp;*</span>\r\n                                    <!-- END product_attribute_mandatory -->\r\n                                </strong><br />\r\n                                {SHOP_PRODCUT_OPTION}\r\n                                <!-- END shopProductOptionsValuesRow -->\r\n                            </div>\r\n                        </div>\r\n                    </div>\r\n                    <!-- END shopProductOptionsRow -->\r\n                    <!-- BEGIN shopProductManufacturer -->\r\n                    <div class=\"manufacturer\" itemprop=\"manufacturer\" itemscope itemtype=\"http://schema.org/Organization\">\r\n                        <meta itemprop=\"name\" content=\"{SHOP_MANUFACTURER_NAME}\" />\r\n                        <meta itemprop=\"url\" content=\"{SHOP_MANUFACTURER_URL}\" />\r\n                        <h3>{TXT_SHOP_MANUFACTURER}</h3>\r\n                        {SHOP_MANUFACTURER_LINK}\r\n                    </div>\r\n                    <!-- END shopProductManufacturer -->\r\n                </div>\r\n            </div>\r\n            <div class=\"functions\">\r\n                <button class=\"add-to-cart\" type=\"submit\" onclick=\"{SHOP_PRODUCT_SUBMIT_FUNCTION}\" name=\"{SHOP_PRODUCT_SUBMIT_NAME}\">{TXT_ADD_TO_CARD}</button>\r\n                <div class=\"price\">\r\n                    <!-- BEGIN price -->\r\n                    <span itemprop=\"priceCurrency\">{SHOP_PRODUCT_PRICE_UNIT}</span> <span itemprop=\"price\">{SHOP_PRODUCT_PRICE}</span>\r\n                    <!-- END price -->\r\n                    <!-- BEGIN price_discount -->\r\n                    statt <span itemprop=\"price\">{SHOP_PRODUCT_PRICE}</span>\r\n                    <span class=\"discount\">\r\n                        nur <span itemprop=\"priceCurrency\">{SHOP_PRODUCT_DISCOUNTPRICE_UNIT}</span> <span itemprop=\"price\">{SHOP_PRODUCT_DISCOUNTPRICE}</span>\r\n                    </span>\r\n                    <!-- END price_discount -->\r\n                </div>\r\n            </div>\r\n        </form>\r\n    </div>\r\n    <!-- END shopProductRow --><!-- BEGIN no_product -->\r\n    <div class=\"no_product\">\r\n        <h2>{TXT_SHOP_NO_PRODUCTS}</h2>\r\n    </div>\r\n    <!-- END no_product -->\r\n</div>', 'Detaillierte Produktedaten', 'details', '1', '42', '0', '34', 'system');
