 <td colspan=\"6\">{TXT_NET_PRICE}</td>\r\n                    <td style=\"text-align: right;\">\r\n                        {SHOP_GRAND_TOTAL_EXCL_TAX} {SHOP_UNIT}\r\n                    </td>\r\n                </tr>\r\n                <!-- END totalExclTaxRow -->\r\n                <!-- BEGIN taxrow -->\r\n                <tr class=\"row1\" valign=\"top\">\r\n                    <td colspan=\"6\">{TXT_TAX_PREFIX}</td>\r\n                    <td nowrap=\"nowrap\" style=\"text-align: right;\">{TXT_TAX_PART} {SHOP_TAX_PRICE} {SHOP_UNIT}</td>\r\n                </tr>\r\n                <!-- END taxrow -->\r\n                <tr class=\"row3\">\r\n                    <td colspan=\"6\" valign=\"top\" nowrap=\"nowrap\"><strong>{TXT_TOTAL_PRICE}</strong></td>\r\n                    <td nowrap=\"nowrap\" style=\"text-align: right;\"><strong>{SHOP_GRAND_TOTAL} {SHOP_UNIT}</strong></td>\r\n                </tr>\r\n            </table>\r\n            <table class=\"order-address\" summary=\"\" cellspacing=\"0\">\r\n                <tr style=\"background: #F2F2F2;\">\r\n                    <th width=\"50%\" align=\"left\">{TXT_ADDRESS_CUSTOMER}</th>\r\n                    <th width=\"50%\" align=\"left\">{TXT_SHIPPING_ADDRESS}</th>\r\n                </tr>\r\n                <tr>\r\n                    <td nowrap=\"nowrap\" valign=\"top\">\r\n                        <span>{SHOP_COMPANY}</span>\r\n                        <span>{SHOP_PREFIX} {SHOP_FIRSTNAME} {SHOP_LASTNAME}</span>\r\n                        <span>{SHOP_ADDRESS}</span>\r\n                        <span>{SHOP_ZIP} {SHOP_CITY}</span>\r\n                        <span>{SHOP_COUNTRY}</span>\r\n                        <span>{SHOP_PHONE}</span>\r\n                        <span>{SHOP_FAX}</span>\r\n                        <span>{SHOP_EMAIL}</span>\r\n                    </td>\r\n                    <td nowrap=\"nowrap\" valign=\"top\">\r\n                        <!-- BEGIN shopShipmentAddress -->\r\n                      