INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `displayorder`, `username`) VALUES ('42', '16', '<div id=\"shop\">\r\n    <!-- BEGIN core_message -->\r\n    <span class=\"{MESSAGE_CLASS}\">{MESSAGE_TEXT}</span><!-- END core_message -->\r\n    <div class=\"menu\">\r\n        <form name=\"shopSearch\" action=\"index.php?section=shop\" method=\"post\">\r\n            <select name=\"catId\" tabindex=\"2\">\r\n            {SHOP_CATEGORIES_MENUOPTIONS}\r\n            </select>&nbsp;\r\n            <input type=\"text\" name=\"term\" value=\"{SHOP_SEARCH_TERM}\" tabindex=\"1\" style=\"width: 170px;\" />&nbsp;\r\n            <input type=\"submit\" name=\"bsubmit\" value=\"{TXT_SHOP_SEARCH}\" />\r\n        </form>\r\n    </div>\r\n    <!-- BEGIN categories -->\r\n    <div class=\"categories\">\r\n        <h2>{TXT_SHOP_CATEGORIES}</h2>\r\n        <!-- BEGIN subCategoriesRow -->\r\n        <!-- BEGIN subCategories -->\r\n        <article class=\"col-13 info-item\">\r\n            <figure class=\"visual\">\r\n                <a href=\"index.php?section=shop{MODULE_INDEX}&amp;catId={SHOP_CATEGORY_ID}\">\r\n                    <div class=\"categorie-image-wrapper\">\r\n                        <img border=\"0\" alt=\"{TXT_SHOP_GO_TO_CATEGORY}\" src=\"{SHOP_CATEGORY_THUMBNAIL}\" />\r\n                    </div>\r\n                    <h3>{SHOP_CATEGORY_NAME}</h3>\r\n                </a>\r\n            </figure>\r\n        </article>\r\n        <!-- END subCategories -->\r\n        <br style=\"clear: both;\" />\r\n        <!-- END subCategoriesRow -->\r\n    </div>\r\n    <!-- END categories -->\r\n    <!-- BEGIN products -->\r\n    <div class=\"informations\">\r\n        <h2>{SHOP_PRODUCTS_IN_CATEGORY}</h2>\r\n    </div>\r\n    <!-- BEGIN shopProductRow -->\r\n    <div class=\"product\">\r\n        <form id=\"{SHOP_PRODUCT_FORM_NAME}\" name=\"{SHOP_PRODUCT_FORM_NAME}\" action=\"index.php?section=shop&amp;cmd=cart\" method=\"post\" {SHOP_FORM_ENCTYPE}>\r\n            <input type=\"hidden\" name=\"productId\" value=\"{SHOP_PRODUCT_ID}\" />\r\n            <div class=\"images\">\r\n                <!-- BEGIN productImage1 -->\r\n                <a href=\"{SHOP_PRODUCT_THUMBNAIL_LINK_1}\">\r\n                    <img border=\"0\" src=\"{SHOP_PRODUCT_THUMBNAIL_1}\" alt=\"{TXT_SEE_LARGE_PICTURE}\" />\r\n                </a>\r\n                <!-- END productImage1 -->\r\n            </div>\r\n            <div class=\"info\">\r\n                <h3>{SHOP_PRODUCT_TITLE}</h3>\r\n                <div class=\"short-description\">\r\n                    {SHOP_PRODUCT_DESCRIPTION}\r\n                    <!-- BEGIN shopProductOptionsRow -->\r\n                    <div class=\"shop_options\">\r\n                        {SHOP_PRODUCT_OPTIONS_TITLE}<br />\r\n                        <div id=\"product_options_layer{SHOP_PRODUCT_ID}\" style=\"display: none;\">\r\n                            <div class=\"shop_options_click\">\r\n                                <!-- BEGIN shopProductOptionsValuesRow -->\r\n                                <strong>\r\n                                    {SHOP_PRODUCT_OPTIONS_NAME}\r\n                                    <!-- BEGIN product_attribute_mandatory -->\r\n                                    <span class=\"mandatory\">&nbsp;*</span>\r\n                                    <!-- END product_attribute_mandatory -->\r\n                                </strong><br />\r\n                                {SHOP_PRODCUT_OPTION}\r\n                                <!-- END shopProductOptionsValuesRow -->\r\n                            </div>\r\n                        </div>\r\n                    </div>\r\n                    <!-- END shopProductOptionsRow -->\r\n                </div>\r\n                <div class=\"details\">\r\n                    <button class=\"details-button\" type=\"button\" onclick=\"location.href=\'{SHOP_PRODUCT_DETAIL_URL}\'\">{TXT_SHOP_PRODUCT_DETAILS}</button>\r\n                </div>\r\n            </div>\r\n            <div class=\"functions\">\r\n                <button class=\"add-to-cart\" type=\"submit\" onclick=\"{SHOP_PRODUCT_SUBMIT_FUNCTION}\" name=\"{SHOP_PRODUCT_SUBMIT_NAME}\">{TXT_ADD_TO_CARD}</button>\r\n                <div class=\"price\">\r\n                    <!-- BEGIN price -->\r\n                    <span itemprop=\"priceCurrency\">{SHOP_PRODUCT_PRICE_UNIT}</span> <span itemprop=\"price\">{SHOP_PRODUCT_PRICE}</span>\r\n                    <!-- END price -->\r\n                    <!-- BEGIN price_discount -->\r\n                    statt <span itemprop=\"price\">{SHOP_PRODUCT_PRICE}</span>\r\n                    <span class=\"discount\">\r\n                        nur <span itemprop=\"priceCurrency\">{SHOP_PRODUCT_DISCOUNTPRICE_UNIT}</span> <span itemprop=\"price\">{SHOP_PRODUCT_DISCOUNTPRICE}</span>\r\n                    </span>\r\n                    <!-- END price_discount -->\r\n                </div>\r\n            </div>\r\n        </form>\r\n    </div>\r\n    <!-- END shopProductRow -->\r\n    <!-- END products -->\r\n    <!-- BEGIN no_product -->\r\n    <div class=\"no_product\">\r\n        <h2>{TXT_SHOP_NO_PRODUCTS}</h2>\r\n    </div>\r\n    <!-- END no_product -->\r\n    <div class=\"paging\">{SHOP_PRODUCT_PAGING}</div>\r\n</div>', 'Online Shop', '', '1', '0', '1', '25', 'system');
