END login_social_networks_google -->\r\n        <!-- BEGIN login_social_networks_twitter -->\r\n        <a class=\"twitter loginbutton\" href=\"{LOGIN_SOCIALLOGIN_TWITTER}\">Twitter</a>\r\n        <!-- END login_social_networks_twitter -->\r\n    </div>\r\n    <!-- END login_social_networks -->\r\n</div>', 'Login', '', '1', '0', '0', '293', 'system');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `displayorder`, `username`) VALUES ('48', '19', '<div id=\"docsys\">\r\n	<div class=\"categories\">\r\n		<form method=\"post\" action=\"index.php?section=docsys\" name=\"docSys\">\r\n			<select name=\"category\" onchange=\"javascript:this.form.submit()\">\r\n			<option selected=\"selected\" value=\"\">{DOCSYS_NO_CATEGORY}</option>\r\n			{DOCSYS_CAT_MENU}\r\n			</select>\r\n		</form>\r\n	</div>\r\n	<div class=\"doc_list\">\r\n        <!-- BEGIN table -->\r\n		<table cellspacing=\"0\">\r\n			<tbody>\r\n				<tr>\r\n					<th width=\"5%\" nowrap=\"nowrap\">Datum</th>\r\n					<th width=\"100%\">Titel</th>\r\n					<th nowrap=\"nowrap\">Kategorie</th>\r\n				</tr>\r\n				<!-- BEGIN row -->\r\n				<tr>\r\n					<td nowrap=\"nowrap\">{DOCSYS_DATE}</td>\r\n					<td width=\"100%\">{DOCSYS_LINK}</td>\r\n					<td nowrap=\"nowrap\">{DOCSYS_CATEGORY}</td>\r\n				</tr>\r\n				<!-- END row -->\r\n			</tbody>\r\n		</table>\r\n        <!-- END table -->\r\n        <!-- BEGIN nothing_found -->\r\n        {TXT_NO_DOCUMENTS_FOUND}\r\n        <!-- END nothing_found -->\r\n	</div>\r\n	<div calss=\"paging\">{DOCSYS_PAGING}</div>\r\n</div>\r\n\r\n', 'Dokumenten System', '', '1', '0', '1', '186', 'system');
