INSERT INTO `contrexx_module_podcast_medium` (`id`, `title`, `youtube_id`, `author`, `description`, `source`, `thumbnail`, `template_id`, `width`, `height`, `playlenght`, `size`, `views`, `status`, `date_added`) VALUES ('9', 'Social Networking - Der große Trend im Internet', 'GSuJwQxU6a8', '', 'Die Idee ist einfach: Menschen mit gleichen Interessen treffen sich auf Internetportalen, um z. B. Persönliches auszutauschen oder für den Beruf eine Gemeinschaft aufzubauen. Die Nutzerzahlen steigen rasant. Doch bei allen \"Social Networking\"-Portalen gibt es noch keine zündende Idee, wie man damit auch richtig Geld verdienen kann.', 'http://youtube.com/v/GSuJwQxU6a8', 'images/podcast/youtube_thumbnails/youtube_GSuJwQxU6a8.jpg', '101', '425', '350', '291', '0', '1', '1', '1292236792');
