INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `displayorder`, `username`) VALUES ('16', '10', '<div id=\"guestbook\">\r\n    <!-- BEGIN guestbookForm -->\r\n    <div class=\"error_message\">{ERROR}</div>\r\n    <div class=\"information\">Sie können sich hier ins Gästebuch eintragen.<br />Damit das Eintragen funktioniert, müssen alle mit einem <font color=\"red\">*</font> markierten Felder ausgefüllt sein.</div>\r\n    <div class=\"form\">\r\n        <form method=\"post\" action=\"index.php?section=guestbook&cmd=post\" name=\"GuestbookForm\">\r\n            <p><label for=\"forename\">Vorname<font color=\"red\"> *</font></label><input type=\"text\" value=\"{FORENAME}\" name=\"forename\" id=\"forename\" size=\"60\" maxlength=\"255\" /></p>\r\n            <p><label for=\"name\">Nachname<font color=\"red\"> *</font></label><input type=\"text\" value=\"{NAME}\" name=\"name\" id=\"name\"  maxlength=\"255\" /></p>\r\n            <p><label for=\"comment\">Kommentar<font color=\"red\"> *</font></label><textarea cols=\"1\" id=\"comment\" name=\"comment\">{COMMENT}</textarea></p>\r\n            <p><label>Geschlecht</label><input type=\"radio\" id=\"female\" name=\"malefemale\" value=\"F\" {FEMALE_CHECKED}/><label class=\"description\" for=\"female\">Weiblich</label></p>\r\n            <p><input type=\"radio\" id=\"male\" name=\"malefemale\" value=\"M\" {MALE_CHECKED}/><label class=\"description\" for=\"male\"> Männlich</label></p>\r\n            <p><label for=\"location\">Wohnort<font color=\"red\"> *</font></label><input type=\"text\" value=\"{LOCATION}\" id=\"location\" name=\"location\" maxlength=\"255\" /></p>\r\n            <p><label for=\"email\">E-Mail<font color=\"red\"> *</font></label><input type=\"text\" value=\"{EMAIL}\" id=\"email\" name=\"email\" maxlength=\"255\" /></p>\r\n            <p><label for=\"url\">Homepage</label><input type=\"text\" maxlength=\"255\" value=\"{HOMEPAGE}\" id