INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `displayorder`, `username`) VALUES ('144', '60', '<div id=\"mediadir\">\r\n    {MEDIADIR_JAVASCRIPT}\r\n\r\n    <!-- BEGIN mediadirEntryOkMessage -->\r\n    Eintrag erfolgreich gespeichert.\r\n    <!-- END mediadirEntryOkMessage -->\r\n\r\n    <!-- BEGIN mediadirEntrySaveOnlyMessage -->\r\n    Ihr Eintrawurde erfolgreich gespeichert, ist aber noch nicht öffentlich einsehbar.<br />Um Ihr Eintragzu veröffentlichen, müssen Sie vor dem Speichervorgang die Option <strong>Freigeben</strong> auswählen.\r\n    <!-- END mediadirEntrySaveOnlyMessage -->\r\n\r\n    <!-- BEGIN mediadirEntryReadyToConfirmMessage -->\r\n    Ihr Eintragwurde erfolgreich gespeichert und wird nun von der Administration geprüft.<br />Nach erfolgter Prüfung wird Ihr Eintrag öffentlich einsehbar sein.<br />Bitte beachten Sie dass die Prüfung einige Zeit in Anspruch nehmen kann. \r\n    <!-- END mediadirEntryReadyToConfirmMessage -->\r\n\r\n    <!-- BEGIN mediadirEntryErrMessage -->\r\n    Bein Speichern trat ein Fehler auf. Bitte versuchen sie es erneut.\r\n    <!-- END mediadirEntryErrMessage -->\r\n    \r\n    <!-- BEGIN mediadirEntryModifyForm -->\r\n    <form enctype=\"multipart/form-data\" method=\"post\" onsubmit=\"{MEDIADIR_FORM_ONSUBMIT}\" action=\"index.php?section=mediadir&amp;cmd=add\" name=\"entryModfyForm\">\r\n    \r\n        <!-- BEGIN mediadirForms -->   \r\n        <table width=\"100%\">\r\n            <!-- BEGIN mediadirFormList -->  \r\n            <tr>\r\n                <td width=\"1%\" valign=\"top\"><input type=\"radio\" name=\"selectedFormId\" value=\"{TXT_MEDIADIR_FORM_ID}\" onclick=\"document.entryModfyForm.submit();\"></td>\r\n                <td width=\"30%\" valign=\"top\">{TXT_MEDIADIR_FORM_IMAGE_THUMB}</td>\r\n                <td width=\"69%\" valign=\"top\"><h2>{TXT_MEDIADIR_FORM_TITLE}</h2>{TXT_MEDIADIR_FORM_DESCRIPTION}</td>\r\n            </tr> \r\n            <!-- END mediadirFormList -->   \r\n        </table>\r\n        <!-- END mediadirForms -->\r\n    \r\n        <!-- BEGIN mediadirInputfields -->\r\n            <!-- BEGIN mediadirEntryAddStepNavigation -->\r\n            <!-- BEGIN mediadirEntryAddStepNavigationElement -->\r\n            <input type=\"button\" value=\"{MEDIADIR_ENTRY_ADDSTEP_NAME}\" id=\"{MEDIADIR_ENTRY_ADDSTEP_ID}\" onclick=\"{MEDIADIR_ENTRY_ADDSTEP_HREF}\" class=\"{MEDIADIR_ENTRY_ADDSTEP_CLASS}\" />\r\n            <!-- END mediadirEntryAddStepNavigationElement -->\r\n            <!-- END mediadirEntryAddStepNavigation -->\r\n\r\n            <input type=\"hidden\" value=\"{MEDIADIR_ENTRY_ID}\" name=\"entryId\" />\r\n            <input type=\"hidden\" value=\"{MEDIADIR_FORM_ID}\" name=\"formId\" />\r\n        \r\n            <!-- BEGIN mediadirInputfieldElement -->   \r\n    \r\n                <!-- BEGIN mediadirInputfieldAddStep -->  \r\n                {MEDIADIR_INPUTFIELD_ADDSTEP}\r\n                <!-- END mediadirInputfieldAddStep -->\r\n    \r\n                <!-- BEGIN mediadirInputfieldList -->   \r\n                <p><label>{TXT_MEDIADIR_INPUTFIELD_NAME}</label>{MEDIADIR_INPUTFIELD_FIELD}</p>\r\n                <!-- END mediadirInputfieldList -->\r\n        \r\n                <!-- BEGIN mediadirTranslationStatus -->\r\n                <p>\r\n                <label>{TXT_MEDIADIR_TRANSLATION_STATUS}</label>\r\n                <span id=\"mediadirInputfield_TranslationStatus_list\" style=\"display: block;\">\r\n                      <!-- BEGIN mediadirTranslationLangList -->\r\n                      <input class=\"marketplaceInputfieldCheckbox\" name=\"translationStatus[]\" id=\"mediadirInputfield_TranslationStatus\" value=\"{MEDIADIR_TRANSLATION_LANG_ID}\" type=\"checkbox\" {MEDIADIR_TRANSLATION_LANG_STATUS} />&nbsp;{TXT_MEDIADIR_TRANSLATION_LANG_NAME}<br>\r\n                      <!-- END mediadirTranslationLangList -->\r\n                </span>\r\n                </p>